mapper.select:
select * from software

    and software_name = #{softwareName}

limit #{offset}, #{pageSize}

select count(*) from software
and software_name = #{softwareName}
and tags like concat('%', #{tag}, '%')
SELECT DISTINCT svd.software_id, svd.version_type, s.software_name, svd.price FROM software_version_download AS svd LEFT JOIN user_software_auth AS usa ON svd.software_id = usa.software_id AND svd.version_type = usa.version_type AND usa.user_id = #{userId} AND usa.expired_time > now() LEFT JOIN software AS s ON s.software_id = svd.software_id WHERE (svd.software_id, svd.version_type) != (usa.software_id, usa.version_type) OR (usa.software_id IS NULL AND usa.version_type IS NULL);
